package com.testjavasel;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 2;
	    int y = 2;
		
		if (x>y) {
			System.out.println("x is greater");
		}
		else if (x==y) {
			System.out.println("x & y are same");
		}
		else {
			System.out.println("y is greater");
		}
	}
}